mapper.select:
SELECT r.id, r.product_id, p.name AS product_name, r.create_date, r.date, r.people, file.name AS file_name, file.type FROM reservation AS r LEFT JOIN product_info AS p ON r.product_id = p.product_id LEFT JOIN file ON r.product_id = file.product_id WHERE file.name LIKE "%_ma_%"
AND r.product_id=#{productId}
AND r.member_id LIKE #{memberId}

mapper.update:
UPDATE reservation

	date=#{date},
	people=#{people}

WHERE id=#{id}
